				title: "Container Assets test",
				controller: 'ContainerAssetsTestController',
				template: [
					'<div class="standaloneAncillaryPage">',
					'	<itt-nav on-logout="logout()"></itt-nav>',
					'	<div>',
					'		<div sxs-container-assets="containerId" mime-key="assetLib"></div>',
					'	</div>',
					'</div>'].join(''),
